  label_replace(
    label_replace(
      label_replace(
        windows_net_nic_address_info{instance="$server"},
        "Address", "$1", "address", "(.*)"
      ),
      "Interface", "$1", "friendly_name", "(.*)"
    ),
  "NIC", "$1", "nic", "(.*)"
  )
)
